// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

contract ContentVerifier {
    struct Verification {
        bool verified;
        uint256 timestamp;
    }

    // Mapping to store verified content along with its timestamp
    mapping(bytes32 => Verification) public verifiedContent;
    
    // Event emitted when content is verified, including timestamp
    event ContentVerified(bytes32 indexed contentHash, address indexed verifier, uint256 timestamp);
    
    // Function to verify content and store its timestamp
    function verifyContent(string memory content) public {
        bytes32 contentHash = keccak256(abi.encodePacked(content));
        require(!verifiedContent[contentHash].verified, "Content already verified");
        
        verifiedContent[contentHash] = Verification(true, block.timestamp);
        emit ContentVerified(contentHash, msg.sender, block.timestamp);
    }
    
    // Function to check if content is verified, returning (verified, timestamp)
    function isVerified(string memory content) public view returns (bool, uint256) {
        bytes32 contentHash = keccak256(abi.encodePacked(content));
        Verification memory verification = verifiedContent[contentHash];
        return (verification.verified, verification.timestamp);
    }
}

